  Procedure Use_Task_Manager_Save
  (
    i_Company_Id number,
    i_Value      varchar2
  ) is
    v_Value varchar2(1) := 'Y';
  begin
    if i_Value <> 'Y' then
      v_Value := 'N';
    end if;
  
    Md_Api.Preference_Save(i_Company_Id => i_Company_Id,
                           i_Filial_Id  => Md_Pref.Filial_Head(i_Company_Id),
                           i_Code       => Hes_Pref.c_Pref_Staff_Use_Task_Manager,
                           i_Value      => v_Value);
  end;

end Hes_Api;
