                                    i_Schedule_Id => i_Schedule_Id);
    else
      z_Hpd_Page_Schedules.Delete_One(i_Company_Id => i_Company_Id,
                                      i_Filial_Id  => i_Filial_Id,
                                      i_Page_Id    => i_Page_Id);
    end if;
  end;

