    end loop;
  
    Unnecessary_Pages_Delete(i_Company_Id => i_Journal.Company_Id,
                             i_Filial_Id  => i_Journal.Filial_Id,
                             i_Journal_Id => i_Journal.Journal_Id,
                             i_Page_Ids   => v_Page_Ids);
  end;

