  ----------------------------------------------------------------------------------------------------
  Function Journal_Type_Id
  (
    i_Company_Id number,
    i_Pcode      varchar2
  ) return number Result_Cache is
    result number;
  begin
    select q.Journal_Type_Id
      into result
      from Hpd_Journal_Types q
     where q.Company_Id = i_Company_Id
       and q.Pcode = i_Pcode;
  
    return result;
  exception
    when No_Data_Found then
      Hpd_Error.Raise_043;
  end;

