      Fazo.Push(v_Types, Hpd_Pref.c_Employment_Type_Contractor);
      Fazo.Push(v_Translates, t_Employment_Type_Contractor);
    end if;
  
    return Matrix_Varchar2(v_Types, v_Translates);
  end;

