  Function Vacancy_Group_Id_By_Pcode
  (
    i_Company_Id number,
    i_Pcode      varchar2
  ) return number;
  ----------------------------------------------------------------------------------------------------  
  Function Vacancy_Type_Id_By_Pcode
  (
    i_Company_Id number,
    i_Pcode      varchar2
