  begin
    v_Type.Vacancy_Group_Id := i_Vacancy_Group_Id;
    v_Type.Vacancy_Type_Ids := i_Vacancy_Type_Ids;
  
    o_Vacancy.Vacancy_Types.Extend();
    o_Vacancy.Vacancy_Types(o_Vacancy.Vacancy_Types.Count) := v_Type;
  end;

