    o_Operation.Note             := i_Note;
  end;

  ----------------------------------------------------------------------------------------------------
  Function Stage_Id_By_Pcode
  (
    i_Company_Id number,
    i_Pcode      varchar2
  ) return number is
    v_Stage_Id number;
  begin
    select q.Stage_Id
      into v_Stage_Id
      from Hrec_Stages q
     where q.Company_Id = i_Company_Id
       and q.Pcode = i_Pcode;
  
    return v_Stage_Id;
  exception
    when No_Data_Found then
      return null;
  end;

  ----------------------------------------------------------------------------------------------------
  Function Funnel_Id_By_Pcode
  (
    i_Company_Id number,
    i_Pcode      varchar2
  ) return number is
    v_Funnel_Id number;
  begin
    select q.Funnel_Id
      into v_Funnel_Id
      from Hrec_Funnels q
     where q.Company_Id = i_Company_Id
       and q.Pcode = i_Pcode;
  
    return v_Funnel_Id;
  exception
    when No_Data_Found then
      return null;
  end;

  ----------------------------------------------------------------------------------------------------  
  Function Vacancy_Group_Id_By_Pcode
  (
    i_Company_Id number,
    i_Pcode      varchar2
  ) return number is
    v_Group_Id number;
  begin
    select q.Vacancy_Group_Id
      into v_Group_Id
