     where q.Company_Id = i_Company_Id
       and q.Pcode = i_Pcode;
  
    return v_Type_Id;
  exception
    when No_Data_Found then
      return null;
  end;

  ----------------------------------------------------------------------------------------------------
  Function Company_Id_By_Employer_Code(i_Employer_Code varchar2) return number is
    v_Company_Id number;
  begin
    select t.Company_Id
      into v_Company_Id
      from Hrec_Hh_Employer_Ids t
     where t.Employer_Id = i_Employer_Code;
  
    return v_Company_Id;
  exception
