  ----------------------------------------------------------------------------------------------------
  Procedure Timebook_Lock_Interval_Insert
  (
    i_Company_Id  number,
    i_Filial_Id   number,
    i_Timebook_Id number,
    i_Staff_Id    number,
    i_Begin_Date  date,
    i_End_Date    date
  ) is
  begin
    Hpd_Core.Timebook_Lock_Interval_Insert(i_Company_Id  => i_Company_Id,
                                           i_Filial_Id   => i_Filial_Id,
