    end if;
  
    z_Hpd_Applications.Update_One(i_Company_Id     => i_Company_Id,
                                  i_Filial_Id      => i_Filial_Id,
                                  i_Application_Id => i_Application_Id,
                                  i_Status         => Option_Varchar2(Hpd_Pref.c_Application_Status_Canceled),
