                             i_Journal_Id   => r.Journal_Id,
                             i_Page_Id      => r.Page_Id,
                             i_Staff_Id     => r.Staff_Id,
                             i_Begin_Date   => r.Change_Date,
                             i_End_Date     => null,
                             i_Order_No     => i_Journal.Posted_Order_No,
                             i_Source_Table => Zt.Hpd_Wage_Changes);
    
    end loop;
  end;

