    return z_Hrm_Robots.Load(i_Company_Id => r_Closest_Robot.Company_Id,
                             i_Filial_Id  => r_Closest_Robot.Filial_Id,
                             i_Robot_Id   => r_Closest_Robot.Robot_Id);
