  ----------------------------------------------------------------------------------------------------
  Function Wage_From_To_Message
  (
    i_Wage_From number,
    i_Wage_To   number
  ) return varchar2 is
    result varchar2(500 char);
  begin
    if i_Wage_From is not null then
      result := t('wage from $1', i_Wage_From);
    end if;
  
    if i_Wage_To is not null then
      result := t('wage to $1', i_Wage_To);
    end if;
  
    return result;
  end;

