    return Matrix_Varchar2(Array_Varchar2(Hrec_Pref.c_Olx_Advertiser_Type_Private,
                                          Hrec_Pref.c_Olx_Advertiser_Type_Businnes),
                           Array_Varchar2(t('olx_advertiser_type:private'),
                                          t('olx_advertiser_type:businnes')));
  end;

end Hrec_Util;
