    o_Template.Indicators := Href_Pref.Indicator_Nt();
    o_Template.Oper_Types := Href_Pref.Oper_Type_Nt();
  end;

  ----------------------------------------------------------------------------------------------------
  Procedure Job_Temp_Add_Oper_Type
  (
    p_Template      in out nocopy Hrm_Pref.Job_Template_Rt,
