  type Transfer_Nt is table of Transfer_Rt;
  ----------------------------------------------------------------------------------------------------
  type Transfer_Journal_Rt is record(
    Company_Id      number,
    Filial_Id       number,
    Journal_Id      number,
    Journal_Type_Id number,
    Journal_Number  varchar2(50 char),
