  ----------------------------------------------------------------------------------------------------
  -- Sick Leave
  ----------------------------------------------------------------------------------------------------
  type Sick_Leave_Rt is record(
    Timeoff_Id        number,
    Staff_Id          number,
    Reason_Id         number,
