          i_S1      => t('004:solution:change access add item of contract $1{contract_id} to yes and try again',
                         i_Contract_Id));
  end;

  ----------------------------------------------------------------------------------------------------
  Procedure Raise_005
  (
    i_Fact_Id     number,
