    Error(i_Code    => '006',
          i_Message => t('006:message:request date period must be smaller than $1 days',
                         i_Request_Max_Days));
  end;

  ----------------------------------------------------------------------------------------------------
  Procedure Raise_007
  (
    i_Error_Code    varchar2,
    i_Error_Message varchar2
  ) is
  begin
    Error(i_Code    => '007',
          i_Message => t('007:message:Billz API returned an error. code = $1, message = $2',
                         i_Error_Code,
                         i_Error_Message));
