          i_Message => t('009:message:you do not have access to create/update request'));
  end;

  ----------------------------------------------------------------------------------------------------
  Procedure Raise_010 is
  begin
    Error(i_Code    => '010',
          i_Message => t('010:message:you do not have access to create/update change'));
  end;

end Hes_Error;
