  end;

  ----------------------------------------------------------------------------------------------------
  Function Error_Map
  (
    i_Code      varchar2,
    i_Message   varchar2,
    i_Title     varchar2 := null,
    i_Solutions Array_Varchar2 := Array_Varchar2()
  ) return Hashmap is
