  ---------------------------------------------------------------------------------------------------
  Procedure Raise_043
  (
    i_Training_Number varchar2,
    i_Status_Name     varchar2
  ) is
  begin
    Error(i_Code    => '043',
          i_Message => t('043:message:to assess person in training, status must be execute, training number: $1, current status: $2',
