  ----------------------------------------------------------------------------------------------------
  Procedure Attestation_Add_Testing
  (
    p_Attestation in out nocopy Hln_Pref.Attestation_Rt,
    i_Testing_Id  number,
    i_Person_Id   number,
    i_Exam_Id     number
  );
  ----------------------------------------------------------------------------------------------------
  Procedure Assert_Access_Person
  (
    i_Company_Id number,
    i_Filial_Id  number,
    i_Testing_Id number,
    i_Person_Id  number
