  Function t_Notification_Action_Attach
  (
    i_Person_Type   varchar2,
    i_Action_Number varchar2,
    i_Begin_Time    date,
    i_End_Time      date := null,
    i_Is_Period     varchar2 := null,
    i_Action_Kind   varchar2
