    i_Document_Id number
  ) is
  begin
    Htm_Core.Recommended_Rank_Document_Status_Approved(i_Company_Id  => i_Company_Id,
                                                       i_Filial_Id   => i_Filial_Id,
                                                       i_Document_Id => i_Document_Id);
  end;

end Htm_Api;
