       where Dm.Company_Id = i_Timesheet.Company_Id
         and Dm.Filial_Id = i_Timesheet.Filial_Id
         and Dm.Schedule_Id = i_Timesheet.Schedule_Id
         and Dm.Schedule_Date = i_Timesheet.Timesheet_Date;
