          i_S1      => t('120:solution:turn off daily limit from $1{calendar_name}', i_Calendar_Name),
          i_S2      => t('120:solution:reduce plan time'),
          i_S3      => t('120:solution:remove $1{calendar_name} from $2{schedule_name}',
